/* Create a file record using the "SysFile" object and save the file content tothe storage immediately. */public bool CreateFileWithData(UserConnection userConnection){    /* Retrieve the "SysFile" object. */    EntitySchema fileSchema = userConnection.EntitySchemaManager.GetInstanceByName("SysFile");    /* Create a file record using the "Entity" class. */    Entity fileEntity = fileSchema.CreateEntity(userConnection);    /* Populate the record using default column values. */    fileEntity.SetDefColumnValues();    /* Set the file name. */    fileEntity.SetColumnValue("Name", "some-file-name.txt");    /* Convert the file content to a byte array. */    byte[] fileContent = System.Text.Encoding.UTF8.GetBytes("Some file content");    /* Assign the binary file content to the "Data" column. */    fileEntity.SetBytesValue("Data", fileContent);    /* Save the file record and persist the content to the storage. */    bool result = fileEntity.Save();    /* Return the result of the save operation. */    return result;}