/* Create a file record using the "SysFile" object without saving file content. */public Guid CreateFileMetadataOnly(UserConnection userConnection){    /* Retrieve the "SysFile" object. */    EntitySchema fileSchema = userConnection.EntitySchemaManager.GetInstanceByName("SysFile");    /* Create a file record using the "Entity" class. */    Entity fileEntity = fileSchema.CreateEntity(userConnection);    /* Populate the record using default column values. */    fileEntity.SetDefColumnValues();    /* Set the file name. */    fileEntity.SetColumnValue("Name", "SomeFileName.pdf");    /* Save the file record metadata without file content. */    fileEntity.Save();    /* Return the ID of the created file record. */    return fileEntity.PrimaryColumnValue;}/* Add binary file content to an existing file record and save it to the storage. */public bool AddFileContent(UserConnection userConnection, Guid fileId){    /* Retrieve the "SysFile" object. */    EntitySchema fileSchema = userConnection.EntitySchemaManager.GetInstanceByName("SysFile");    /* Create a file record using the "Entity" class. */    Entity fileEntity = fileSchema.CreateEntity(userConnection);    /* Load the existing file record by its ID. */    if (!fileEntity.FetchFromDB(fileId))    {        return false;    }    /* Retrieve the file content as a byte array.    "GetPdfContent()" is a custom method that returns file content. */    byte[] fileContent = GetPdfContent();    /* Assign the binary file content to the "Data" column. */    fileEntity.SetBytesValue("Data", fileContent);    /* Save the file record and persist the content to the storage. */    return fileEntity.Save();}