/* Create a file record using the "ContactFile" object and bind it to a contact. */public bool CreateContactFile(UserConnection userConnection, Guid contactId){    /* Retrieve the "ContactFile" object. */    EntitySchema fileSchema = userConnection.EntitySchemaManager.GetInstanceByName("ContactFile");    /* Create a file record using the "Entity" class. */    Entity fileEntity = fileSchema.CreateEntity(userConnection);    /* Populate the record using default column values. */    fileEntity.SetDefColumnValues();    /* Set the file name. */    fileEntity.SetColumnValue("Name", "SomeContactPhoto.jpg");    /* Bind the file record to the contact record. */    fileEntity.SetPropertyValue("ContactId", contactId);    /* Retrieve the file content as a byte array.    "GetImageBytes()" is a custom method that returns image data. */    byte[] imageData = GetImageBytes();    /* Assign the binary file content to the "Data" column. */    fileEntity.SetBytesValue("Data", imageData);    /* Save the file record and persist the content to the storage. */    return fileEntity.Save();}