/* Create a file record using the "SysFile" object and bind it to a parent recordusing a polymorphic relationship. */public bool CreateSysFile(UserConnection userConnection, Guid recordId, string schemaName){    /* Retrieve the "SysFile" object. */    EntitySchema fileSchema = userConnection.EntitySchemaManager.GetInstanceByName("SysFile");    /* Create a file record using the "Entity" class. */    Entity fileEntity = fileSchema.CreateEntity(userConnection);    /* Populate the record using default column values. */    fileEntity.SetDefColumnValues();    /* Set the file name. */    fileEntity.SetColumnValue("Name", "SomeFileName.pdf");    /* Bind the file record to the parent record by specifying the code of the    parent "Object" type schema and the ID of the parent record to which the file    is attached. */    fileEntity.SetColumnValue("RecordSchemaName", schemaName);    fileEntity.SetColumnValue("RecordId", recordId);    /* Retrieve the file content as a byte array.    "GetPdfContent()" is a custom method that returns file content. */    byte[] fileContent = GetPdfContent();    /* Assign the binary file content to the "Data" column. */    fileEntity.SetBytesValue("Data", fileContent);    /* Save the file record and persist the content to the storage. */    return fileEntity.Save();}