/* Retrieve the binary content of a file record as a byte array. */public byte[] ReadFileContent(UserConnection userConnection, Guid fileId){    /* Retrieve the "SysFile" object. */    EntitySchema fileSchema = userConnection.EntitySchemaManager.GetInstanceByName("SysFile");    /* Create a file record using the "Entity" class. */    Entity fileEntity = fileSchema.CreateEntity(userConnection);    /* Load the existing file record by its ID. */    if (!fileEntity.FetchFromDB(fileId))    {        return null;    }    /* Retrieve the binary file content from the "Data" column as a byte array. */    byte[] fileContent = fileEntity.GetBytesValue("Data");    /* Return the file content. */    return fileContent;}