/* Update the binary content of an existing file record. */public bool UpdateFileContent(UserConnection userConnection, Guid fileId, byte[] newContent){    /* Retrieve the "SysFile" object. */    EntitySchema fileSchema = userConnection.EntitySchemaManager.GetInstanceByName("SysFile");    /* Create a file record using the "Entity" class. */    Entity fileEntity = fileSchema.CreateEntity(userConnection);    /* Load the existing file record by its ID. */    if (!fileEntity.FetchFromDB(fileId))    {        return false;    }    /* Assign the new binary file content to the "Data" column. */    fileEntity.SetBytesValue("Data", newContent);    /* Update the file name (optional). */    fileEntity.SetColumnValue("Name", "NewFileName.txt");    /* Save the file record and persist the updated content to the storage. */    return fileEntity.Save();}