/* Delete an existing file record along with its binary content. */public bool DeleteFile(UserConnection userConnection, Guid fileId){    /* Retrieve the "SysFile" object. */    EntitySchema fileSchema = userConnection.EntitySchemaManager.GetInstanceByName("SysFile");    /* Create a file record using the "Entity" class. */    Entity fileEntity = fileSchema.CreateEntity(userConnection);    /* Load the existing file record by its ID. */    if (!fileEntity.FetchFromDB(fileId))    {        return false;    }    /* Delete the file record. Both the metadata and the file content are removed from the storage. */    bool result = fileEntity.Delete();    /* Return the result of the delete operation. */    return result;}