using System;using System.Web;using Terrasoft.Core;using Terrasoft.Core.Entities;namespace CreatioCustomServerLogic.Cloud {    public class MyContactReader: IExecutor {        public void Execute(UserConnection userConnection) {            /* Retrieve the instance of the [Contacts] schema. */            var entitySchema = userConnection.EntitySchemaManager.GetInstanceByName("Contact");            /* Create an instance of the request class. */            var esq = new EntitySchemaQuery(entitySchema);            /* Add all schema columns to the request. */            esq.AddAllSchemaColumns();            /* Retrieve the collection of [Contacts] section records. */            var collection = esq.GetEntityCollection(userConnection);            foreach(var entity in collection) {                /* Pass the needed values to the HTTP response to the Executor utility request. */                HttpContext.Current.Response.Write(entity.GetTypedColumnValue <string> ("Name"));                HttpContext.Current.Response.Write(Environment.NewLine);            }        }    }}