namespace Terrasoft.Configuration.UsrEncodingServiceNamespace{    using System;    using System.ServiceModel;    using System.ServiceModel.Web;    using System.ServiceModel.Activation;    using Terrasoft.Core;    using Terrasoft.Web.Common;    using Terrasoft.Core.Entities;    [ServiceContract]    [AspNetCompatibilityRequirements(        RequirementsMode = AspNetCompatibilityRequirementsMode.Required)]    public class UsrEncodingService : BaseService    {        private SystemUserConnection _systemUserConnection;        private SystemUserConnection SystemUserConnection        {            get            {                return _systemUserConnection ??                    (_systemUserConnection =                        (SystemUserConnection)                            AppConnection.SystemUserConnection);            }        }        /* The method that returns the value of the passed parameter in the        specified encoding. */        [OperationContract]        [WebInvoke(            Method = "POST",            RequestFormat = WebMessageFormat.Xml,            BodyStyle = WebMessageBodyStyle.Wrapped,            ResponseFormat = WebMessageFormat.Xml)]        public string ReceiveEncodedText(string EncodedText)        {            /* The user on whose behalf to process the HTTP request. */            SessionHelper.SpecifyWebOperationIdentity(                HttpContextAccessor.GetInstance(),                SystemUserConnection.CurrentUser);            /* Return the result. */            return EncodedText;        }    }}