public void ProcessEmployeeOnboarding(Guid userId, Guid departmentId) {    /* Check the business rule condition.    Continue only if the user is a manager in the head department. */    if (!IsManagerInHeadDepartment(userId, departmentId)) {        return;    }    /* Retrieve the list of roles required for managers in the head department. */    var roles = GetHeadDepartmentManagerRoles();    /* Assign the user to all required roles. */    foreach (var roleId in roles) {        AssignUserToRole(userId, roleId);    }    /* Redistribute licenses for the user based on the newly assigned roles. */    var service = ClassFactory.Get<ILicenseRedistributionService>();    service.RedistributeLicencesForUser(userId);}