/* The file content. */var content = new byte[]();/* Create the "EntityFileLocator" instance identified by "recordId" and stored inthe "ActivityFile" database table. */var fileLocator = new EntityFileLocator("ActivityFile", recordId);/* Pass the created locator to the method of the "UserConnection" class. */IFile file = UserConnection.GetFile(fileLocator);/* Retrieve the file content and save it to the array. */using (Stream stream = file.Read()) {    content = stream.ReadToEnd();}/* Create a unique ID to copy the current file. */Guid copyFileId = Guid.NewGuid();/* Create the "EntityFileLocator" instance identified by "copyFileId" and stored inthe "ActivityFile" database table. */var copyFileLocator = new EntityFileLocator("ActivityFile", copyFileId);/* Pass the created locator to the method of the "UserConnection" class. */IFile copyFile = UserConnection.CreateFile(copyFileLocator);/* Specify the file name to determine the file in a new file storage. */copyFile.Name = file.Name + " - Copy";/* Save the file metadata before saving the file content. */copyFile.Save();/* Copy the file content from the original file using one of the options.Option 1. Specify "FileWriteOptions.SinglePart" to save the content without breakingit into multiple parts. */copyFile.Write(new MemoryStream(content), FileWriteOptions.SinglePart);/* Option 2. */file.Copy(copyFile); /* Create a unique ID to paste the copied file. */Guid moveFileId = Guid.NewGuid();/* Create the "EntityFileLocator" instance identified by "moveFileId" and storedin the "ContactFile" database table. */var moveFileLocator = new EntityFileLocator("ContactFile", moveFileId);/* Pass the created locator to the method of the "UserConnection" class. */IFile moveFile = UserConnection.CreateFile(moveFileLocator);/* Save the file metadata before saving the file content. */moveFile.Save(); /* Paste the copied file to a new file storage. */file.Move(moveFile); /* Delete the file from original file storage. */file.Delete();