SELECT    [Country].[Name] [Country.Name],    [Country1].[Name] [Country1.Name],    [CreatedBy].[Name] [CreatedBy.Name]FROM    [dbo].[City] [City]    LEFT OUTER JOIN [dbo].[Country] [Country] ON ([Country].[Id] = [City].[CountryId])    INNER JOIN [dbo].[Country] [Country1] ON ([Country1].[Id] = [City].[CountryId])    LEFT OUTER JOIN [dbo].[Country] [Country2] ON ([Country2].[Id] = [City].[CountryId])    RIGHT OUTER JOIN [dbo].[Contact] [CreatedBy] ON ([CreatedBy].[Id] = [Country2].[CreatedById])