/* Receive the contact name. */string userName = Entity.GetTypedColumnValue<string>("Name");/* Receive the date of the contact birthday. */DateTime birthDate = Entity.GetTypedColumnValue<DateTime>("BirthDate");/* Generate the message text. */string messageText = "{\"birthday\": \"" + birthDate.ToString("s") + "\", \"name\": \"" + userName + "\"}";/* Set the message name. */string sender = "NewMessage";/* Publish the message via WebSocket. */MsgChannelUtilities.PostMessageToAll(sender, messageText);return true;