var fileName = "file.zip";var filePath = "SomeFilePath/file.zip";FileInfo fileInfo = new FileInfo(filePath);using (FileStream fileStream = new FileStream(filePath, FileMode.Open, FileAccess.Read)){    int uploadChunkLength = 10000;    byte[] filePart = new byte[uploadChunkLength];    int totalSize = (int) fileInfo.Length;    int uploadedSize = 0;    int currentChunkLength;    while ((currentChunkLength = fileStream.Read(filePart, 0, filePart.Length)) != 0){        int num = uploadedSize == 0 ? 0 : uploadedSize + 1;        byte[] currentFileChunk = new byte[currentChunkLength];        Array.Copy(filePart, currentFileChunk, currentChunkLength);        using (MemoryStream memoryStream = new MemoryStream()) {            memoryStream.Write(currentFileChunk, 0, currentChunkLength);            var request = new HttpRequestMessage(HttpMethod.Post, $"localhost/0/rest/FileApiService/UploadFile?fileId=51bc924d-7341-cdbd-0a98-b04a4e6d04cc&totalFileLength={totalSize}&mimeType=application%2Fx-zipcompressed&fileName={fileName}&columnName=Data&entitySchemaName=ContactFile&parentColumnName=Contact&parentColumnValue=c4ed336c-3e9b-40fe8b82-5632476472b4");            request.Headers.Add("Content-Range", $"bytes {num}-{uploadedSize + currentFileChunk.Length}/{totalSize}");            request.Headers.Add("Content-Disposition", $"attachment; filename={fileName}");        }        uploadedSize += currentChunkLength;        num = totalSize - uploadedSize;        uploadChunkLength = num < uploadChunkLength ? num : uploadChunkLength;        filePart = new byte[uploadChunkLength];        Console.WriteLine($"Loaded {uploadedSize} from {totalSize}");    }}