public string GetEntitiesExample() {    var result = "";    /* Create a query to the City schema, add the Name column to the query. */    var esqResult = new EntitySchemaQuery(UserConnection.EntitySchemaManager, "City");    var colName = esqResult.AddColumn("Name");    /* Run the database query and retrieve the entire resulting objects collection. */    var entities = esqResult.GetEntityCollection(UserConnection);    for (int i = 0; i < entities.Count; i++) {        result += entities[i].GetColumnValue(colName.Name).ToString();        result += "\n";    }    /* Run the database query and retrieve the object that has the set identifier. */    var entity = esqResult.GetEntity(UserConnection, new Guid("100B6B13-E8BB-DF11-B00F-001D60E938C6"));    result += "\n";    result += entity.GetColumnValue(colName.Name).ToString();    return result;}