public Collection <string> UsingCacheExample() {    /* Create a query to the City schema, add the Name column to the query. */    var esqResult = new EntitySchemaQuery(UserConnection.EntitySchemaManager, "City");    esqResult.AddColumn("Name");    /* Define the key the cache will use to store the query results. Creatio will use the session level Creatio cache with local storage since the Cache object property is not redefined. */    esqResult.CacheItemName = "EsqResultItem";    /* The collection to place the query results. */    var esqCityNames = new Collection <string> ();    /* The collection to place the cached query results. */    var cachedEsqCityNames = new Collection <string> ();    /* Run the database query and retrieve the resulting objects collection. Creatio will cache the query results after this operation. */    var entities = esqResult.GetEntityCollection(UserConnection);    /* Process the query results and populate the esqCityNames collection. */    foreach(var entity in entities) {        esqCityNames.Add(entity.GetTypedColumnValue <string> ("Name"));    }    /* Retrieve the link to the esqResult query cache as a data table in memory using the CacheItemName key. */    var esqCacheStore = esqResult.Cache[esqResult.CacheItemName] as DataTable;    /* Populate the cachedEsqCityNames collection using the query cache values. */    if (esqCacheStore != null) {        foreach(DataRow row in esqCacheStore.Rows) {            cachedEsqCityNames.Add(row[0].ToString());        }    }    return cachedEsqCityNames;}