public Collection <string > ESQOptionsExample() {    /* Create a query instance that has the City root schema. */    var esqCities = new EntitySchemaQuery(UserConnection.EntitySchemaManager, "City");    esqCities.AddColumn("Name");    /* Create a query that has the Country root schema. */    var esqCountries = new EntitySchemaQuery(UserConnection.EntitySchemaManager, "Country");    esqCountries.AddColumn("Name");    /* Create an instance of settings to return first 5 strings in the query. */    var esqOptions = new EntitySchemaQueryOptions() {        PageableDirection = PageableSelectDirection.First,            PageableRowCount = 5,            PageableConditionValues = new Dictionary <string, object> ()    };    /* Retrieve the city collection that contains the first 5 cities of the resulting dataset. */    var cities = esqCities.GetEntityCollection(UserConnection, esqOptions);    /* Retrieve the country collection that contains the first 5 countries of the resulting dataset. */    var countries = esqCountries.GetEntityCollection(UserConnection, esqOptions);    var esqStringCollection = new Collection <string> ();    foreach(var entity in cities) {        esqStringCollection.Add(entity.GetTypedColumnValue <string> ("Name"));    }    foreach(var entity in countries) {        esqStringCollection.Add(entity.GetTypedColumnValue <string> ("Name"));    }    return esqStringCollection;}