namespace Terrasoft.Configuration{    using Terrasoft.Core.Campaign;    using Terrasoft.Core.DB;    /* Define a custom campaign element. */    public class UsrSmsCampaignProcessElement : CampaignFlowElement    {        public UsrSmsCampaignProcessElement() {        }        /* Assign the "PhoneNumber" attribute value in the class. */        public string PhoneNumber {            get;            set;        }        /* Assign the "SmsText" attribute value in the class. */        public string SmsText {            get;            set;        }        /* Define the campaign audience for the element. */        protected override Query GetAudienceQuery() =>            new Select(UserConnection)                .Column("Id")                .From(CampaignParticipantTable)                .Where("CampaignItemId").IsEqual(Column.Parameter(CampaignItemId))                .And("StatusId")                    .IsEqual(Column.Parameter(                        CampaignConstants.CampaignParticipantParticipatingStatusId                    ))                .And("StepCompleted").IsEqual(Column.Parameter(0));        /* Execute the campaign step logic for the selected audience. */        protected override int SingleExecute(Query audienceQuery) {            return SetItemCompleted(audienceQuery as Select);        }    }}