// Converting a JSON object string to a byte array.byte[] jsonArray = Encoding.UTF8.GetBytes(json);// Creating an instance of HTTP request.var updateRequest = HttpWebRequest.Create(updateQueryUri) as HttpWebRequest;// Defining a request method.updateRequest.Method = "POST";// Determining type of request content.updateRequest.ContentType = "application/json";// Adding authentication cookie received earlier to a request.updateRequest.CookieContainer = AuthCookie;// Set length for request content.updateRequest.ContentLength = jsonArray.Length;// Putting BPMCSRF token to the request header.CookieCollection cookieCollection = AuthCookie.GetCookies(new Uri(authServiceUri));string csrfToken = cookieCollection["BPMCSRF"].Value;updateRequest.Headers.Add("BPMCSRF", csrfToken);// Plase a JSON-object to request content.using (var requestStream = updateRequest.GetRequestStream()){    requestStream.Write(jsonArray, 0, jsonArray.Length);}// Executing HTTP request and getting a response from server.using (var response = (HttpWebResponse)updateRequest.GetResponse()){    // Displaying response in console.    using (StreamReader reader = new StreamReader(response.GetResponseStream()))    {        Console.WriteLine(reader.ReadToEnd());    }}