public string CreateESQ() {    var result = "";    /* Retrieve the instance of the object schema manager. */    EntitySchemaManager esqManager = SystemUserConnection.EntitySchemaManager;    /* Retrieve the instance of the schema to set as root for the EntitySchemaQuery instance. */    var rootEntitySchema = esqManager.GetInstanceByName("City") as EntitySchema;    /* Create the "EntitySchemaQuery" instance that has the "rootEntitySchema" set as root. */    var esqResult = new EntitySchemaQuery(rootEntitySchema);    /* Add columns to select in the resulting query. */    esqResult.AddColumn("Id");    esqResult.AddColumn("Name");    /* Retrieve the Select instance associated with the "EntitySchemaQuery" query. */    Select selectEsq = esqResult.GetSelectQuery(SystemUserConnection);    /* Retrieve the text of the "EntitySchemaQuery" instance's resulting query. */    result = selectEsq.GetSqlText();    return result;}