public string CreateESQClone() {    var result = "";    EntitySchemaManager esqManager = SystemUserConnection.EntitySchemaManager;    var esqSource = new EntitySchemaQuery(esqManager, "Contact");    esqSource.AddColumn("Id");    esqSource.AddColumn("Name");    /* Clone the "EntitySchemaQuery" instance from the "esqSource" instance. */    var esqClone = new EntitySchemaQuery(esqSource);    result = esqClone.GetSelectQuery(SystemUserConnection).GetSqlText();    return result;}