-- Function-- PostgreSQLDROP FUNCTION IF EXISTS "public"."fn_IsGuid";CREATE OR REPLACE FUNCTION public."fn_IsGuid"(ValidateValue IN VARCHAR) RETURNS BOOLEAN AS $$    BEGIN        IF (regexp_matches(ValidateValue, '^\{?[0-9a-fA-F]{8}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{12}\}?$'))[1] IS NOT NULL THEN            RETURN TRUE;        ELSE            RETURN FALSE;        END IF;    END;$$ LANGUAGE plpgsql;