/* The user connection. */var userConnection = (UserConnection) HttpContext.Current.Session["UserConnection"];/* Retrieve the ID of the needed language. */var sysCulture = new SysCulture(userConnection);/* Return an error message if the primary language is not found. */if (!sysCulture.FetchPrimaryInfoFromDB("Name", "it-IT")) {    return "No culture found";}/* Retrieve ID of the Italian language. */Guid italianCultureId = sysCulture.Id;/* Create an EntitySchemaQuery instance that has the "City" root schema. */var esqResult = new EntitySchemaQuery(userConnection.EntitySchemaManager, "City");/* Add a column to the query. */esqResult.AddColumn("Name");/* Pass the ID of the Italian language. */esqResult.SetLocalizationCultureId(italianCultureId);/* Execute a database query and retrieve the resulting object collection. */var entities = esqResult.GetEntityCollection(userConnection);/* Retrieve the query text. */var s = esqResult.GetSelectQuery(userConnection).GetSqlText();/* Return the result. */return s;