/* The user connection. */var userConnection = (UserConnection)HttpContext.Current.Session["UserConnection"];/* Retrieve the "Account type" schema. */EntitySchema schema = userConnection.EntitySchemaManager.FindInstanceByName("AccountType");/* Create the "Entity" instance. */Entity entity = schema.CreateEntity(userConnection);/* Set the default column values. */entity.SetDefColumnValues();/* Create a localizable string that includes localized values for German and English languages. */var localizableString = new LocalizableString();localizableString.SetCultureValue(new CultureInfo("de-DE"), "Neuer Kunde de-DE");localizableString.SetCultureValue(new CultureInfo("en-US"), "New Customer en-US");/* Set the "Name" column value using the localizable string. */entity.SetColumnValue("Name", localizableString);/* Save the changes. */entity.Save();/* Display the result in the language chosen in the user profile. */var name = String.Format("Name: {0}", entity.GetTypedColumnValue<string>("Name"));return name;