namespace Terrasoft.Configuration {    using System;    using System.Globalization;    using Terrasoft.Common;    using Terrasoft.Core;    using Terrasoft.Core.Entities;    public class CreateJobTitle: IInstallScriptExecutor {        public void Execute(UserConnection userConnection) {            EntitySchemaManager entitySchemaManager = userConnection.EntitySchemaManager;            Entity job = entitySchemaManager.GetEntityByName("Job", userConnection);            job.SetDefColumnValues();            job.SetColumnValue("Id", Guid.NewGuid());            var jobName = new LocalizableString();            var esCulture = new CultureInfo("es-ES");            var enCulture = new CultureInfo("en-US");            jobName.SetCultureValue(esCulture, "Mentor");            jobName.SetCultureValue(enCulture, "Master");            job.SetColumnValue("Name", jobName);            job.Save();        }    }}