/* The user connection. */var userConnection = (UserConnection)HttpContext.Current.Session["UserConnection"];/* Retrieve the "Account type" schema. */EntitySchema schema = userConnection.EntitySchemaManager.FindInstanceByName("AccountType");/* Create the "Entity" instance. */Entity entity = schema.CreateEntity(userConnection);/* Set the default column values. */entity.SetDefColumnValues();/* Set the "Name" column value. */entity.SetColumnValue("Name", "Nouveau Client");/* Save the changes. */entity.Save();/* Display the result in the language chosen in the user profile. */var name = String.Format("Name: {0}", entity.GetTypedColumnValue<string>("Name"));return name;