-- Function-- Microsoft SQLIF  EXISTS (SELECT * FROM sys.objects        WHERE object_id = OBJECT_ID(N'[dbo].[fn_IsGuid]') AND type = N'FN')DROP FUNCTION [dbo].[fn_IsGuid]GOCREATE FUNCTION [dbo].[fn_IsGuid] (    @ValidateValue NVARCHAR(MAX))RETURNS BITASBEGIN    DECLARE @hasLeftBraces BIT    IF @ValidateValue LIKE '{%'    BEGIN        SET @ValidateValue = SUBSTRING(@ValidateValue, 2, LEN(@ValidateValue) - 1)        SET @hasLeftBraces = 1    END ELSE    BEGIN        SET @hasLeftBraces = 0    END    DECLARE @hasRightBraces BIT    IF @ValidateValue LIKE '%}'    BEGIN        SET @ValidateValue = SUBSTRING(@ValidateValue, 1, LEN(@ValidateValue) - 1)        SET @hasRightBraces = 1    END ELSE    BEGIN        SET @hasRightBraces = 0    END    DECLARE @Result BIT    IF @ValidateValue LIKE '[0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F]-[0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F]-[0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F]-[0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F]-[0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F][0-9a-fA-F]'    BEGIN        SET @Result = 1    END ELSE    BEGIN        SET @Result = 0    END    IF @hasLeftBraces = @hasRightBraces    BEGIN        RETURN @Result    END ELSE    BEGIN        SET @Result = 0    END    RETURN @ResultENDGO