-- View and triggers that let you modify the target table-- PostgreSQLDROP FUNCTION IF EXISTS "public"."ITR_VwSysLookup_IUD_Func" CASCADE;DROP VIEW IF EXISTS "public"."VwSysLookup";CREATE VIEW "public"."VwSysLookup" ASSELECT "SysLookup"."Id"    ,"SysLookup"."CreatedOn"    ,"SysLookup"."CreatedById"    ,"SysLookup"."ModifiedOn"    ,"SysLookup"."ModifiedById"    ,"SysLookup"."Name"    ,"SysLookup"."Description"    ,"SysLookup"."SysFolderId"    ,"SysLookup"."SysEntitySchemaUId"    ,"SysLookup"."SysGridPageSchemaUId"    ,"SysLookup"."SysEditPageSchemaUId"    ,"VwSysSchemaInfo"."SysWorkspaceId"    ,"SysLookup"."ProcessListeners"    ,"SysLookup"."IsSystem"    ,"SysLookup"."IsSimple"FROM "public"."SysLookup"INNER JOIN "public"."VwSysSchemaInfo" ON "SysLookup"."SysEntitySchemaUId" = "VwSysSchemaInfo"."UId";CREATE FUNCTION "public"."ITR_VwSysLookup_IUD_Func"() RETURNS TRIGGER AS $$    BEGIN        IF TG_OP = 'INSERT' THEN            INSERT INTO "public"."SysLookup"(                "Id"                ,"CreatedOn"                ,"CreatedById"                ,"ModifiedOn"                ,"ModifiedById"                ,"Name"                ,"Description"                ,"SysFolderId"                ,"SysEntitySchemaUId"                ,"SysGridPageSchemaUId"                ,"SysEditPageSchemaUId"                ,"ProcessListeners"                ,"IsSystem"                ,"IsSimple")            SELECT NEW."Id"                ,NEW."CreatedOn"                ,NEW."CreatedById"                ,NEW."ModifiedOn"                ,NEW."ModifiedById"                ,NEW."Name"                ,NEW."Description"                ,NEW."SysFolderId"                ,NEW."SysEntitySchemaUId"                ,NEW."SysGridPageSchemaUId"                ,NEW."SysEditPageSchemaUId"                ,NEW."ProcessListeners"                ,NEW."IsSystem"                ,NEW."IsSimple";            RETURN NEW;        ELSIF TG_OP = 'UPDATE' THEN            UPDATE "public"."SysLookup"            SET "CreatedOn" = NEW."CreatedOn"                ,"CreatedById" = NEW."CreatedById"                ,"ModifiedOn" = NEW."ModifiedOn"                ,"ModifiedById" = NEW."ModifiedById"                ,"Name" = NEW."Name"                ,"Description" = NEW."Description"                ,"SysFolderId" = NEW."SysFolderId"                ,"SysEntitySchemaUId" = NEW."SysEntitySchemaUId"                ,"SysGridPageSchemaUId" = NEW."SysGridPageSchemaUId"                ,"SysEditPageSchemaUId" = NEW."SysEditPageSchemaUId"                ,"ProcessListeners" = NEW."ProcessListeners"                ,"IsSystem" = NEW."IsSystem"                ,"IsSimple" = NEW."IsSimple"            WHERE "SysLookup"."Id" = NEW."Id";            RETURN NEW;        ELSIF TG_OP = 'DELETE' THEN            DELETE FROM "public"."SysLookup" WHERE OLD."Id" = "SysLookup"."Id";            RETURN OLD;        END IF;        RETURN NEW;    END;$$ LANGUAGE plpgsql;CREATE TRIGGER "ITR_VwSysLookup_IUD"    INSTEAD OF INSERT OR UPDATE OR DELETE ON "public"."VwSysLookup"    FOR EACH ROW EXECUTE PROCEDURE "public"."ITR_VwSysLookup_IUD_Func"();