public static void SetContactField(UserConnection userConnection, string contactName, string columnValue) {    /* The ContactHelper class is a helper class. */    var entity = ContactHelper.GetContact(userConnection, contactName);    /* The LanguageHelper class is a helper class. */    entity.SetColumnValue("ProgrammingLanguageId", LanguageHelper.GetLanguageId(userConnection, columnValue));    entity.Save();}