namespace Terrasoft.Configuration {    [Terrasoft.Core.Factories.Override]    public class UsrSubstituteClass: UsrOriginalClass {        /* Rate. Assign the property value in the class. */        public int Rate {            get;            private set;        }        /* Initialize the Rate property in the constructor using the passed value. */        public UsrSubstituteClass(int rateValue) {            Rate = rateValue;        }        /* Replace the parent method using the custom implementation. */        public override int GetAmount(int substValue1, int substValue2) {            return (substValue1 + substValue2) * Rate;        }    }}