/* Create a unique ID for the new file. */Guid recordId = Guid.NewGuid();/* Create the "EntityFileLocator" instance identified by "recordId" and stored in the "ActivityFile" database table. */var fileLocator= new EntityFileLocator("ActivityFile", recordId);/* Pass the created locator to the method of the "UserConnection" class. */IFile file = UserConnection.CreateFile(fileLocator);/* Specify the file name to determine the file in the file storage. */file.Name = "Some name of a new";/* Bind the file to a single activity using "activityId." */file.SetAttribute("ActivityId", activityId);/* Save the file metadata before saving the file content. */file.Save();/* Specify the file content. */var content = new byte[] {0x12, 0x34, 0x56};using (var stream = new MemoryStream(content)) {    /* Save the file content to the database. Specify "FileWriteOptions.SinglePart" to save the content without breaking it into multiple parts. */    file.Write(stream, FileWriteOptions.SinglePart);}