/* The user connection. */var userConnection = (UserConnection) HttpContext.Current.Session["UserConnection"];/* Retrieve the "Account type" schema. */EntitySchema schema = userConnection.EntitySchemaManager.FindInstanceByName("AccountType");/* Create the "Entity" instance. */Entity entity = schema.CreateEntity(userConnection);/* The collection of column names to generate a selection. */List<string>columnNamesToFetch = new List<string>{    "Name",    "Description"};/* Retrieve object data whose "Name" column value is "Customer." */entity.FetchFromDB("Name", "Customer", columnNamesToFetch);/* Display the result in the language chosen in the user profile. */var name = String.Format("Name: {0}", entity.GetTypedColumnValue<string>("Name"));return name;