new Select(userConnection)    .Column("a", "Id")    .Column("a", "CountryId")    .Column("a", "EmployeesNumberId")    .Column("a", "IndustryId")    .Column("a", "AccountCategoryId")    .Column("c", "Name").As("AccountCategory")    .From("Account").As("a")    .InnerJoin("AccountCategory").As("c")        .On("a", "AccountCategoryId").IsEqual("c", "Id")    .Where("a", "CreatedOn")        .IsGreater(Func.DateAddDay(-90, Func.CurrentDateTime()))