using NUnit.Framework;namespace Terrasoft.Configuration.Tests.sdkNUnitTests {    [TestFixture]    class UsrNUnitSourceCodeTests {        /* The tested class instance. */        UsrNUnitSourceCode objToTest = new UsrNUnitSourceCode();        /* The testing string. */        string str = "String to test";        [Test]        public void ClassReturnsCorrectStringProperty() {            /* Test the string property value.            The value must be populated and match the required value. */            string res = objToTest.StringToTest;            Assert.That(res, Is.Not.Null.And.EqualTo(str));        }        [Test]        public void StringsMustBeEqual() {            /* Test the value equality of the two strings. */            bool res = objToTest.AreStringsEqual(str, "String to test");            Assert.That(res, Is.True);        }        [Test]        public void StringsMustBeNotEqual() {            /* Test the value inequality of the two strings.            This test will fail since the values are equal. */            bool res = objToTest.AreStringsEqual(str, "String to test");            Assert.That(res, Is.False);        }    }}