public static void CreateContact(UserConnection userConnection, string contactName, string accountName) {    if (GetContact(userConnection, contactName) != null) {        return;    }    EntitySchemaManager entitySchemaManager = userConnection.EntitySchemaManager;    Entity contact = entitySchemaManager.GetEntityByName("Contact", userConnection);    contact.SetDefColumnValues();    contact.SetColumnValue("Id", Guid.NewGuid());    /* The AccountHelper class is a helper class. */    contact.SetColumnValue("AccountId", AccountHelper.GetAccountId(userConnection, accountName));    contact.SetColumnValue("Name", contactName);    contact.Save();}