public static void CreateObjectViaWcf(){    /* Create a new contact and initialize its properties. */    var contact = new Contact()    {        Id = Guid.NewGuid(),        Name = "Some user name"    };    /* Create a new account to which the contact is connected and initialize the account properties. */    var account = new Account()    {        Id = Guid.NewGuid(),        Name = "Some company"    };    contact.Account = account;    /* Create the Creatio app context. */    var context = new Creatio(serverUri);    /* Define the method that adds authentication cookies upon creating a new request. The "AuthenticateExternalRequests()" method authenticates the user and adds the cookies received in response to the data request. */    context.SendingRequest += new EventHandler<SendingRequestEventArgs>(AuthenticateExternalRequests);    /* Add the contact to the service data model's contact collection. */    context.AddToAccountCollection(account);    /* Add the account to the service data model's account collection. */    context.AddToContactCollection(contact);    /* Connect the contact to the account in the service data model. */    context.SetLink(contact, "Account", account);    /* Save the changes to Creatio in a single request. */    DataServiceResponse responses = context.SaveChanges(SaveChangesOptions.Batch);    /* Implement the custom business logic that handles server responses. */}