public bool UpdateEntity() {    /* Create a query to the Order schema, add all schema columns to the query. */    var esqResult = new EntitySchemaQuery(UserConnection.EntitySchemaManager, "Order");    esqResult.AddAllSchemaColumns();    /* Run the database query and retrieve the object that has the set identifier. You can retrieve the object UId from the browser navigation bar after opening the section record page. */    var entity = esqResult.GetEntity(UserConnection, new Guid("58be5223-715d-4b16-a5c4-e3d4ec0412d9"));    /* Create the data string object of the OrderStatus schema. */    var statusSchema = UserConnection.EntitySchemaManager.GetInstanceByName("OrderStatus");    var newStatus = statusSchema.CreateEntity(UserConnection);    /* Retrieve the object that has the set identifier from the database. */    newStatus.FetchFromDB("Name", "4. Completed");    /* Assign the new value to the StatusId column. */    entity.SetColumnValue("StatusId", newStatus.GetTypedColumnValue <Guid> ("Id"));    /* Save the modified object to the database. */    var result = entity.Save();    return result;}