DECLARE     -- The name of the mini page view schema.@ClientUnitSchemaName NVARCHAR(100) = 'UsrProductMiniPage',    -- The name of the object schema to bind the mini page.@EntitySchemaName NVARCHAR(100) = 'Product'UPDATE SysModuleEditSET MiniPageSchemaUId = (    SELECT TOP 1 UId    FROM SysSchema    WHERE Name = @ClientUnitSchemaName)WHERE SysModuleEntityId = (    SELECT TOP 1 Id    FROM SysModuleEntity    WHERE SysEntitySchemaUId = (        SELECT TOP 1 UId        FROM SysSchema        WHERE Name = @EntitySchemaName            AND ExtendParent = 0    ));