// Converting a JSON object string in a byte array.byte[] jsonArray = Encoding.UTF8.GetBytes(json);// Creating an instance of HTTP request.var batchRequest = HttpWebRequest.Create(deleteQueryUri) as HttpWebRequest;// Defining request method.batchRequest.Method = "POST";// Determining request content.batchRequest.ContentType = "application/json";// Adding authentication cookie received earlier to a query.batchRequest.CookieContainer = AuthCookie;// Set the ContentLength property of the WebRequest.batchRequest.ContentLength = jsonArray.Length;// Adding CSRF token to the request header.CookieCollection cookieCollection = AuthCookie.GetCookies(new Uri(authServiceUri));string csrfToken = cookieCollection["BPMCSRF"].Value;batchRequest.Headers.Add("BPMCSRF", csrfToken);// Adding JSON object to the query contents.using (var requestStream = batchRequest.GetRequestStream()){    requestStream.Write(jsonArray, 0, jsonArray.Length);}// Executing HTTP request and getting reply from server.using (var response = (HttpWebResponse)batchRequest.GetResponse()){    // Displaying response in console.    using (StreamReader reader = new StreamReader(response.GetResponseStream()))    {        Console.WriteLine(reader.ReadToEnd());    }}// Application execution delay.Console.ReadKey();