/* Declare a replaced class. */public class SubstitutableClass{/* The class property to initialize in the constructor. */public int OriginalValue { get; private set; }    /* The default constructor that initializes the OriginalValue property with 10. */    public SubstitutableClass()    {        OriginalValue = 10;    }    /* The method that returns the OriginalValue multiplied by 2. You can redefine this method in replacing classes. */    public virtual int GetMultipliedValue()    {        return OriginalValue * 2;    }}