public bool InsertEntity(string city, string country) {    city = city ?? "unknown city";    country = country ?? "unknown country";    var citySchema = UserConnection.EntitySchemaManager.GetInstanceByName("City");    var entity = citySchema.CreateEntity(UserConnection);    entity.FetchFromDB("Name", city);    /* Set the default values for the object columns. */    entity.SetDefColumnValues();    var contryEntity = new Entity(UserConnection, new Guid("09FCE1F8-515C-4296-95CD-8CD93F79A6CF"));    contryEntity.FetchFromDB("Name", country);    /* Assign the passed city name to the Name column. */    entity.SetColumnValue("Name", city);    /* Assign the passed country UId to the CountryId column. */    entity.SetColumnValue("CountryId", contryEntity.GetTypedColumnValue <Guid> ("Id"));    var result = entity.Save();    return result;}