namespace Terrasoft.Configuration.Omnichannel.Messaging {    using System;    using System.Collections.Generic;    using Terrasoft.Core;    using Terrasoft.Core.DB;    #region Class: ForEveryoneOperatorRoutingRule    /// <summary>    /// Retrieve chat agents.    /// </summary>    public class TestOperatorRoutingRule: BaseOperatorRoutingRule {        #region Constructors: Public        /// <summary>        /// Initialize a new instance of <see cref="TestOperatorRoutingRule"/>.        /// </summary>        /// <param name="userConnection"><see cref="UserConnection"/> the instance.</param>        public TestOperatorRoutingRule(UserConnection userConnection) : base(userConnection) {}        #endregion        #region Methods: Private        #endregion        #region Methods: Protected        /// <inheritdoc cref="BaseOperatorRoutingRule.PickUpFreeQueueOperators(Guid, Guid)"/>        protected override List < Guid > PickUpFreeQueueOperators(Guid chatId, Guid queueId) {            return new List < Guid > {                Guid.Parse("7F3B869F-34F3-4F20-AB4D-7480A5FDF647")            };        }        /// <inheritdoc cref="BaseOperatorRoutingRule.GetChatOperator(Guid)"/>        protected override Guid GetChatOperator(Guid chatId) {            Guid operatorId = (new Select(UserConnection).Column("OperatorId").From("OmniChat", "oc").Where("oc", "Id").IsEqual(Column.Parameter(chatId))            as Select).ExecuteScalar < Guid > ();            return operatorId;        }        #endregion    }    #endregion}