UserConnection userConnection = GetUserConnection();IProcessExecutor processExecutor = userConnection.ProcessEngine.ProcessExecutor;/* Key-value collection of input business process parameters where key is the parameter code (the "Code" property value) and the value is the parameter value. */var inputParameters = new Dictionary<string, string> {    ["SomeInputParameter1"] = "SomeInputParameter1Value",    ["SomeInputParameter2"] = "SomeInputParameter2Value"};string processSchemaName = "SomeBusinessProcessCode";Guid processSchemaUId = Guid.Parse("00000000-0000-0000-0000-000000000000");/* Run a business process using the process name. Returns the text value of the "SomeOutputParameter3" parameter. */string resultValue = processExecutor.Execute<string>(processSchemaName, "SomeOutputParameter3");/* Run a business process using the process ID. Returns the text value of the "SomeOutputParameter3" parameter. */string resultValue = processExecutor.Execute<string>(processSchemaName, "SomeOutputParameter3");/* Run a business process using the process name and collection of input process parameters. Returns the text value of the "SomeOutputParameter3" parameter. */string resultValue = processExecutor.Execute<string>(processSchemaName, "SomeOutputParameter3", inputParameters);/* Run a business process using the process ID and collection of input process parameters. Returns the text value of the "SomeOutputParameter3" parameter. */string resultValue = processExecutor.Execute<string>(processSchemaUId, "SomeOutputParameter3", inputParameters);