DECLARE -- The name of the detail schema.@ClientUnitSchemaName NVARCHAR(100) = 'UsrCourierServiceDetail',-- The name of the detail object schema.@EntitySchemaName NVARCHAR(100) = 'UsrCourierService',-- The name of the detail.@DetailCaption NVARCHAR(100) = 'CourierService'INSERT INTO SysDetail(    Caption,    DetailSchemaUId,    EntitySchemaUId)VALUES(    @DetailCaption,    (SELECT TOP 1 UId    from SysSchema    WHERE Name = @ClientUnitSchemaName),    (SELECT TOP 1 UId    from SysSchema    WHERE Name = @EntitySchemaName))