/* Retrieve the value dictionary from the session data storage via the "SomeDictionary" key. */Dictionary<string, string> dic = (Dictionary<string, string>)UserConnection.SessionData["SomeDictionary"];/* Change the values of a dictionary item. Changes are not committed to the storage. */dic["Key"] = "ChangedValue";/* Add an item to the dictionary. Changes are not committed to the storage. */dic.Add("NewKey", "NewValue");/* Commit a dictionary to the data storage via the "SomeDictionary" key. The changes are committed to the storage. */UserConnection.SessionData["SomeDictionary"] = dic;