public static void UpdateObjectViaWcf(){    /* Create the Creatio app context. */    var context = new Creatio(serverUri);    /* Define the method that adds authentication cookies upon creating a new request. The "AuthenticateExternalRequests()" method authenticates the user and adds the cookies received in response to the data request. */    context.SendingRequest += new EventHandler<SendingRequestEventArgs>(AuthenticateExternalRequests);    /* Select a contact to update. */    var updateContact = context.ContactCollection.Where(c =< c.Name.Contains("Some user name")).First();    /* Update the contact properties. */    updateContact.Notes = "Some new description for the contact";    updateContact.Phone = "Some new phone for the contact";    /* Save the changes to the service data model. */    context.UpdateObject(updateContact);    /* Save the changes to Creatio in a single request. */    var responses = context.SaveChanges(SaveChangesOptions.Batch);}