DECLARE -- The name of the detail schema.@ClientUnitSchemaName NVARCHAR(100) = 'UsrDetailSchemaName',-- The name of the detail object schema.@EntitySchemaName NVARCHAR(100) = 'UsrDetailObjectSchemaName',-- The name of the detail.@DetailCaption NVARCHAR(100) = 'DetailName'INSERT INTO SysDetail(    Caption,    DetailSchemaUId,    EntitySchemaUId)VALUES(    @DetailCaption,    (SELECT TOP 1 UId    from SysSchema    WHERE Name = @ClientUnitSchemaName),    (SELECT TOP 1 UId    from SysSchema    WHERE Name = @EntitySchemaName))