/* The key with which to add the value to the storage via the proxy class. */string key = "SomeKey";/* The prefix to add to the key value using the proxy class. */string prefix = "customPrefix";/* Create a proxy class that writes values to the session level data storage. */IDataStore proxyStorage = new DataStoreKeyTransformerProxy(prefix) { DataStore = Store.Data[DataLevel.Session] };/* Write values that have the "SomeKey" key to the storage via the proxy class. Essentially, write the value that has the "prefix + key" key to the global session level data storage. */proxyStorage[key] = "StoredValue";/* Retrieve values that have the "SomeKey" key via the proxy class. */var valueFromProxyStorage = (string)proxyStorage[key];/* Retrieve values that have the "prefix + key" key directly from session level storage. */var valueFromGlobalStorage = (string)UserConnection.SessionData[prefix + key];