[EntityEventListener(SchemaName = "Activity")]public class ActivityEntityEventListener: BaseEntityEventListener {    /* The handler method of the event after the entity is saved. */    public override void OnSaved(object sender, EntityAfterEventArgs e) {        base.OnSaved(sender, e);        /* The instance of the class to execute asynchronously. */        var asyncExecutor = ClassFactory.Get <IEntityEventAsyncExecutor> (            new ConstructorArgument("userConnection", ((Entity) sender).UserConnection));        /* The parameters to execute asynchronously. */        var operationArgs = new EntityEventAsyncOperationArgs((Entity) sender, e);        /* Asynchronous execution. */        asyncExecutor.ExecuteAsync <DoSomethingActivityAsyncOperation> (operationArgs);    }}