/* The key with which to add the value to the cache via the proxy class. */string key = "SomeKey";/* The prefix to add to the key value using the proxy class. */string prefix = "customPrefix";/* Create a proxy class that writes values to the session level cache. */ICacheStore proxyCache = new PrefixKeyTransformerProxy(prefix, Store.Cache[CacheLevel.Session]);/* Write values that have the "SomeKey" key to the cache via the proxy class. Essentially, write the value that has the "prefix + key" key to the global session level cache. */proxyCache[key] = "CachedValue";/* Retrieve values that have the "key" key via the proxy class. */var valueFromProxyCache = (string)proxyCache[SomeKey];/* Retrieve values that have the "prefix + key" key directly from session level cache. */var valueFromGlobalCache = (string)UserConnection.SessionCache[prefix + key];